from stonesoup.scoring.outputs.base import Base_Output


class Standard_Error(Base_Output):
    def __init__(self, metadata, actual):
        super(Standard_Error, self).__init__(metadata, actual)

    def get_string(self, encoding=None):
        return self.get_file_as_string(self.get_log_from_input_id('stderr'), encoding)

    def get_file(self, encoding=None):
        return self.get_file_as_file(self.get_log_from_input_id('stderr'), encoding)
