import unittest
import sys
from stonesoup.metadata.versions.v1_0_0.enums import EncodingType
from stonesoup.scoring.outputs.return_code import Return_Code
from stonesoup.tests.scoring.base import Scoring_Base


class Test(Scoring_Base):

    def test_get_string(self):
        print >> sys.stderr, sys._getframe().f_code.co_name
        actual = self.get_returncode_output_type(EncodingType.string)
        return_code = Return_Code(self.metadata, actual)
        self.assertEqual(return_code.get_string(), '1234')

    def test_get_file(self):
        print >> sys.stderr, sys._getframe().f_code.co_name
