import unittest
import sys
from stonesoup.metadata.versions.v1_0_0.enums import EncodingType
from stonesoup.scoring.outputs.standard_error import Standard_Error
from stonesoup.tests.scoring.base import Scoring_Base
from stonesoup.utils import Workspace


class Test(Scoring_Base):

    def test_get_file(self):
        print >> sys.stderr, sys._getframe().f_code.co_name
        actual = self.get_stderr_output_type(EncodingType.string)
        standard_err = Standard_Error(self.metadata, actual)
        self.assertEqual(standard_err.get_file(), Workspace.make_rel_path_abs("logs/execute/execute.stderr"))

if __name__ == "__main__":
    # import sys;sys.argv = ['', 'Test.testName']
    unittest.main()
